mapper.update:
UPDATE notice SET subject=#{subject},
     content=#{content}, noticeWhether=#{noticeWhether}
     WHERE noticeNum=#{noticeNum}

mapper.select:
SELECT  fileNum, noticeNum, saveFilename, originalFilename, fileSize
    FROM noticeFile
    WHERE  noticeNum=#{noticeNum}

SELECT  fileNum, noticeNum, saveFilename, originalFilename, fileSize
    FROM noticeFile
    WHERE fileNum=#{fileNum}
