mapper.select:
		select IFNULL(count(*), 0) from snotice 
		
			studyNum = #{studyNum}   
			
            	AND

SELECT s.sNoticeNum, nSubject, hitCount, created, snFilenum FROM snotice s LEFT JOIN (select min(snFilenum) snFilenum, sNoticeNum from snoticefile group by sNoticeNum) f ON s.sNoticeNum = f.sNoticeNum
studyNum = #{studyNum}
AND
ORDER BY sNoticeNum DESC LIMIT #{start}, #{rows}
